import os
class project_tools:
    insects_img_dirname="insects_imgs"
    processed_img_dirname="processed_imgs"
    characteristic_dirname="chas_data"
    @staticmethod
    def project_root_path(project_name=None):
        """
        获取当前项目根路径
        :param project_name:
        :return: 根路径
        """
        PROJECT_NAME = 'insects_detection' if project_name is None else project_name
        project_path = os.path.abspath(os.path.dirname(__file__))
        root_path = project_path[:project_path.find(PROJECT_NAME) + len(PROJECT_NAME)]
        return root_path

    @staticmethod
    def prepare_working_dirs(project_root_dir):
        if not os.path.exists(project_root_dir+'\\'+project_tools.insects_img_dirname):
            os.mkdir(project_root_dir+'\\'+project_tools.insects_img_dirname)
        elif not os.path.exists(project_root_dir+'\\'+project_tools.processed_img_dirname):
            os.mkdir(project_root_dir + '\\' + project_tools.processed_img_dirname)
        elif not os.path.exists(project_root_dir + '\\' + project_tools.characteristic_dirname):
            os.mkdir(project_root_dir + '\\' + project_tools.characteristic_dirname)

